import 'package:flutter/material.dart';import 'package:flutter/foundation.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:go_router/go_router.dart';import 'package:nested_routes_bottom_navigation_bar_flutter/navigation_bar/navigation_bar.dart';import '../../../router/go_router_cubit.dart';import '../../../screens/ui.dart';List<GoRoute> _subRoutes = [  GoRoute(    path: 'account',    builder: (context, state) => const AccountScreen(),  ),];class CartRouterCubit extends Cubit<GoRouter> {  CartRouterCubit()      : super(          GoRouterInitial(            routes: [              GoRoute(                  routes: _subRoutes,                  path: '/',                  builder: (context, state) => const CartScreen()),            ],          ),        );}