import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:go_router/go_router.dart';import '../screens/ui.dart';class Category extends StatelessWidget {  const Category({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Router(      routerDelegate: context.read<CategoryRouterCubit>().state.routerDelegate,      routeInformationProvider: context.read<CategoryRouterCubit>().state.routeInformationProvider,      routeInformationParser: context.read<CategoryRouterCubit>().state.routeInformationParser,    );  }}class CategoryRouterCubit extends Cubit<GoRouter> {  CategoryRouterCubit()      : super(    GoRouter(      routes: [        GoRoute(path: '/', builder: (context, state) => const CategoryScreen()),        GoRoute(          path: '/account',          builder: (context, state) => const AccountScreen(),        ),      ],    ),  );}