import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:go_router/go_router.dart';import '../../../screens/ui.dart';class Home extends StatelessWidget {  const Home({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Router(      routerDelegate: context.read<HomeRouterCubit>().state.routerDelegate,      routeInformationProvider: context.read<HomeRouterCubit>().state.routeInformationProvider,      routeInformationParser: context.read<HomeRouterCubit>().state.routeInformationParser,    );  }}class HomeRouterCubit extends Cubit<GoRouter> {  HomeRouterCubit()      : super(          GoRouter(            routes: [              GoRoute(path: '/', builder: (context, state) => const HomeScreen()),              GoRoute(                path: '/account',                builder: (context, state) => const AccountScreen(),              ),            ],          ),        );}