import 'package:flutter/material.dart';class AccountView extends StatefulWidget {  const AccountView({Key? key}) : super(key: key);  @override  State<AccountView> createState() => _AccountViewState();}/// ``` AutomaticKeepAliveClientMixin ``` is used to prevent widget from disposing when changing PageView page./// to stop it just remove the above mixin ``` AutomaticKeepAliveClientMixin ```/// OR change ``` wantKeepAlive ``` to ``` false ```;class _AccountViewState extends State<AccountView> with AutomaticKeepAliveClientMixin {  @override  bool get wantKeepAlive => true;  @override  Widget build(BuildContext context) {    super.build(context);    return Scaffold(      appBar: AppBar(),      body: const Center(        child: Text("Account View"),      ),    );  }}