import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import '../../app.dart';class WebNavigationBar extends StatelessWidget {  final Widget child;  const WebNavigationBar({Key? key, required this.child}) : super(key: key);  @override  Widget build(BuildContext context) {    Widget navigationItem(      String title,      IconData? icon,    ) =>        GestureDetector(          onTap: () async {            String route = title == "Home" ? "/" : "/$title".toLowerCase();            context.read<AppRouterCubit>().state.push(route);          },          child: Column(            mainAxisAlignment: MainAxisAlignment.center,            children: [              Icon(                icon,                color: Colors.blue,                size: 30,              ),              Text(                title,                style: const TextStyle(                  fontWeight: FontWeight.bold,                  fontSize: 15,                ),              ),            ],          ),        );    return Scaffold(      body: Row(        crossAxisAlignment: CrossAxisAlignment.center,        mainAxisAlignment: MainAxisAlignment.center,        children: [          Expanded(            child: Column(              // mainAxisSize: MainAxisSize.min,              children: [                // Spaces.mediumVerticalSpace,                Expanded(                  child: Column(                    mainAxisSize: MainAxisSize.min,                    children: [                      Container(                        color: Colors.white,                        height: 90,                        child: Stack(                          children: [                            Align(                              alignment: Alignment.topCenter,                              child: Row(                                children: [                                  const SizedBox(                                    width: 20,                                  ),                                  navigationItem("Home", Icons.home),                                  const SizedBox(                                    width: 20,                                  ),                                  navigationItem("Cart", Icons.shopping_cart),                                  const SizedBox(                                    width: 20,                                  ),                                  navigationItem("Category", Icons.category),                                  const SizedBox(                                    width: 20,                                  ),                                  Expanded(                                    child: Container(                                      decoration: const BoxDecoration(                                        color: Colors.blue,                                        borderRadius:                                            BorderRadius.all(Radius.circular(50)),                                        boxShadow: [                                          BoxShadow(                                            color: Colors.grey,                                            blurRadius: 0.1,                                          ),                                        ],                                      ),                                      height: 40,                                      child: Padding(                                        padding:                                            const EdgeInsets.symmetric(horizontal: 10),                                        child: Row(                                          children: const [                                            Icon(                                              Icons.search,                                              size: 40,                                            ),                                            Text("Search"),                                          ],                                        ),                                      ),                                    ),                                  ),                                  const SizedBox(                                    width: 20,                                  ),                                  navigationItem("Category", Icons.category),                                  const SizedBox(                                    width: 20,                                  ),                                  navigationItem("Cart", Icons.shopping_cart),                                  const SizedBox(                                    width: 20,                                  ),                                  navigationItem("Home", Icons.home),                                  const SizedBox(                                    width: 20,                                  ),                                ],                              ),                            ),                          ],                        ),                      ),                      Expanded(child: child),                    ],                  ),                ),              ],            ),          ),        ],      ),    );  }}