import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:go_router/go_router.dart';import '../../../account/account.dart';import '../../../app/app.dart';class AccountNavBarItemRouter extends StatelessWidget {  const AccountNavBarItemRouter({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Router(      routerDelegate: context.read<AccountRouterCubit>().state.routerDelegate,      routeInformationProvider: context.read<AccountRouterCubit>().state.routeInformationProvider,      routeInformationParser: context.read<AccountRouterCubit>().state.routeInformationParser,    );  }}class AccountRouterCubit extends Cubit<GoRouter> {  AccountRouterCubit()      : super(          GoRouter(            routes: [              GoRoute(                  path: '/',                  name: RoutesPaths.account,                  builder: (context, state) {                    return const AccountView();                  }),            ],          ),        );}