import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:go_router/go_router.dart';import '../../../cart/cart.dart';import '../../app.dart';class CartNavBarItemRouter extends StatelessWidget {  const CartNavBarItemRouter({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Router(      routerDelegate: context.read<CartRouterCubit>().state.routerDelegate,      routeInformationProvider: context.read<CartRouterCubit>().state.routeInformationProvider,      routeInformationParser: context.read<CartRouterCubit>().state.routeInformationParser,    );  }}class CartRouterCubit extends Cubit<GoRouter> {  CartRouterCubit()      : super(          GoRouter(            routes: [              GoRoute(                  path: '/',                  name: RoutesPaths.cart,                  builder: (context, state) {                    return const CartView();                  }),            ],          ),        );}