import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:go_router/go_router.dart';import '../../../explore/explore.dart';import '../../app.dart';class ExploreNavBarItemRouter extends StatelessWidget {  const ExploreNavBarItemRouter({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Router(      routerDelegate: context.read<ExploreRouterCubit>().state.routerDelegate,      routeInformationProvider: context.read<ExploreRouterCubit>().state.routeInformationProvider,      routeInformationParser: context.read<ExploreRouterCubit>().state.routeInformationParser,    );  }}class ExploreRouterCubit extends Cubit<GoRouter> {  ExploreRouterCubit()      : super(          GoRouter(            routes: [              GoRoute(                  path: '/',                  name: RoutesPaths.explore,                  builder: (context, state) {                    return const ExploreView();                  }),            ],          ),        );}