import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:go_router/go_router.dart';import '../../../home/home.dart';import '../../app.dart';class HomeNavBarItemRouter extends StatelessWidget {  const HomeNavBarItemRouter({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Router(      routerDelegate: context.read<HomeRouterCubit>().state.routerDelegate,      routeInformationProvider: context.read<HomeRouterCubit>().state.routeInformationProvider,      routeInformationParser: context.read<HomeRouterCubit>().state.routeInformationParser,    );  }}class HomeRouterCubit extends Cubit<GoRouter> {  HomeRouterCubit()      : super(          GoRouter(            routes: [              GoRoute(                  path: '/',                  name: RoutesPaths.home,                  builder: (context, state) {                    return const HomeView();                  }),            ],          ),        );}