import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:go_router/go_router.dart';import '../../../orders/orders.dart';import '../../app.dart';class OrdersNavBarItemRouter extends StatelessWidget {  const OrdersNavBarItemRouter({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Router(      routerDelegate: context.read<OrdersRouterCubit>().state.routerDelegate,      routeInformationProvider: context.read<OrdersRouterCubit>().state.routeInformationProvider,      routeInformationParser: context.read<OrdersRouterCubit>().state.routeInformationParser,    );  }}class OrdersRouterCubit extends Cubit<GoRouter> {  OrdersRouterCubit()      : super(          GoRouter(            routes: [              GoRoute(                  path: '/',                  name: RoutesPaths.orders,                  builder: (context, state) {                    return const OrdersView();                  }),            ],          ),        );}