import 'package:flutter/material.dart';class ExploreView extends StatefulWidget {  const ExploreView({Key? key}) : super(key: key);  @override  State<ExploreView> createState() => _ExploreViewState();}/// ``` AutomaticKeepAliveClientMixin ``` is used to prevent widget from disposing when changing PageView page./// to stop it just remove the above mixin ``` AutomaticKeepAliveClientMixin ```/// OR change ``` wantKeepAlive ``` to ``` false ```;class _ExploreViewState extends State<ExploreView> with AutomaticKeepAliveClientMixin {  @override  bool get wantKeepAlive => true;  @override  Widget build(BuildContext context) {    super.build(context);    return Scaffold(      appBar: AppBar(),      body: const Center(        child: Text("Explore View"),      ),    );  }}