import 'package:flutter/material.dart';class HomeView extends StatefulWidget {  const HomeView({Key? key}) : super(key: key);  @override  State<HomeView> createState() => _HomeViewState();}/// ``` AutomaticKeepAliveClientMixin ``` is used to prevent widget from disposing when changing PageView page./// to stop it just remove the above mixin ``` AutomaticKeepAliveClientMixin ```/// OR change ``` wantKeepAlive ``` to ``` false ```;class _HomeViewState extends State<HomeView> with AutomaticKeepAliveClientMixin {  @override  bool get wantKeepAlive => true;  @override  Widget build(BuildContext context) {    super.build(context);    return Scaffold(      appBar: AppBar(),      body: const Center(        child: Text("Home View"),      ),    );  }}