import 'package:flutter/material.dart';class OrdersView extends StatefulWidget {  const OrdersView({Key? key}) : super(key: key);  @override  State<OrdersView> createState() => _OrdersViewState();}/// ``` AutomaticKeepAliveClientMixin ``` is used to prevent widget from disposing when changing PageView page./// to stop it just remove the above mixin ``` AutomaticKeepAliveClientMixin ```/// OR change ``` wantKeepAlive ``` to ``` false ```;class _OrdersViewState extends State<OrdersView> with AutomaticKeepAliveClientMixin {  @override  bool get wantKeepAlive => true;  @override  Widget build(BuildContext context) {    super.build(context);    return Scaffold(      appBar: AppBar(),      body: const Center(        child: Text("Order View"),      ),    );  }}